import React, { useState } from 'react';
import axios from 'axios';
import { FaUser, FaLock } from 'react-icons/fa';
import './Login.css';
import Footer from '../components/Footer';

const Login = ({ onLoginSuccess }) => {
    const [correo, setCorreo] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.get('http://localhost:8000/Validacion_Login/', {
                params: {
                    correo_electronico: correo,
                    password: password,
                },
                withCredentials: true, // 游녣 Para aceptar cookies
            });

            console.log('Login exitoso', response.data);
            onLoginSuccess?.(response.data); // Puedes redirigir o guardar datos aqu칤
        } catch (err) {
            console.error(err);
            setError("Correo o contrase침a incorrectos");
        }
    };

    return (
        <div className="login-container">
            <div className="login-box">
                <h2>Iniciar sesi칩n</h2>
                <form onSubmit={handleLogin}>
                    <div className="input-group">
                        <span className="icon"><FaUser /></span>
                        <input
                            type="text"
                            placeholder="Correo electr칩nico"
                            value={correo}
                            onChange={(e) => setCorreo(e.target.value)}
                            required
                        />
                    </div>

                    <div className="input-group">
                        <span className="icon"><FaLock /></span>
                        <input
                            type="password"
                            placeholder="Contrase침a"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>

                    <div className="options">
                        <label>
                            <input type="checkbox" /> Recu칠rdame
                        </label>
                        <a href="#">쯆lvidaste la contrase침a?</a>
                    </div>

                    <button type="submit" className="login-btn">Iniciar sesi칩n</button>
                    {error && <p style={{ color: 'red', marginTop: '10px' }}>{error}</p>}
                </form>
            </div>
            <Footer />
        </div>
    );
};

export default Login;
