import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import "./Agendamientos.css";
import Sidebar from "../components/Sidebar";

const Agendamientos = () => {
  const [agendamientos, setAgendamientos] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8000/Agendamientos/")
      .then((response) => response.json())
      .then((data) => {
        setAgendamientos(data);
      })
      .catch((error) => console.error("Error fetching agendamientos:", error));
  }, []);

  const eventos = agendamientos.map((agendamiento) => ({
    title: `${agendamiento.nombre_clase || "Clase"} (${agendamiento.estudiante || "Estudiante"})`,
    date: agendamiento.fecha_reserva || agendamiento.fecha_hora,
  }));

  return (
    <div className="wrapper">
      <Sidebar />
      <div className="main-content">
        <h2 className="mb-4">Calendario de Agendamientos</h2>

        <div className="mb-3">
          <button className="btn btn-primary me-2">Agendar Clase</button>
          <button className="btn btn-warning me-2">Modificar</button>
          <button className="btn btn-danger">Cancelar</button>
        </div>

        <FullCalendar
          plugins={[dayGridPlugin]}
          initialView="dayGridMonth"
          events={eventos}
          locale="es"
          height="auto"
        />
      </div>
    </div>
  );
};

export default Agendamientos;
