import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import "./Agendamientos.css"; // Estilos opcionales

const Agendamientos = () => {
  const [clases, setClases] = useState([]);

  useEffect(() => {
    // Llamada al endpoint de FastAPI para obtener los agendamientos
    const fetchAgendamientos = async () => {
      try {
        // Cambia la URL si tu backend está en un puerto diferente
        const response = await fetch("http://localhost:8000/Agendamientos/");
        const data = await response.json();

        // Mapeamos los datos para adaptarlos al formato que necesita FullCalendar
        const eventos = data.map((clase) => ({
          title: `Clase ${clase.id_clase} (Estudiante ${clase.id_estudiante})`,
          date: clase.fecha, // Asegúrate de que la fecha esté en el formato correcto
        }));

        setClases(eventos);
      } catch (error) {
        console.error("Error al obtener los agendamientos:", error);
      }
    };

    fetchAgendamientos();
  }, []); // Se ejecuta solo una vez cuando el componente se monta

  return (
    <div className="container mt-4">
      <h2 className="mb-4">Calendario de Clases</h2>
      <FullCalendar
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        events={clases}
        locale="es"
        height="auto"
      />
    </div>
  );
};

export default Agendamientos;
