import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import "@fortawesome/fontawesome-free/css/all.min.css";
import "./assets/css/sb-admin-2.min.css"; // Asegúrate de importar los estilos de SB Admin 2
import Home from "./pages/Home";
import Dashboard from "./pages/Dashboard";
import Login from "./pages/Login";
import AgendarClase from "./pages/AgendarClase";
import VerClases from "./pages/VerClases";
import Sidebar from "./components/Sidebar"; // Importa el sidebar si existe
import Navbar from "./components/Navbar"; // Importa el topbar si exist

function DashboardLayout({ children }) {
    return (
        <div id="wrapper">
            <Sidebar />
            <div id="content-wrapper" className="d-flex flex-column">
                <div id="content">
                    <Topbar />
                    <div className="container-fluid">{children}</div>
                </div>
            </div>
        </div>
    );
}

function App() {
    return (
        <Router>
            <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/login" element={<Login />} />
                <Route
                    path="/dashboard"
                    element={
                        <DashboardLayout>
                            <Dashboard />
                        </DashboardLayout>
                    }
                />
                <Route
                    path="/agendar"
                    element={
                        <DashboardLayout>
                            <AgendarClase />
                        </DashboardLayout>
                    }
                />
                <Route
                    path="/clases"
                    element={
                        <DashboardLayout>
                            <VerClases />
                        </DashboardLayout>
                    }
                />
            </Routes>
        </Router>
    );
}

export default App;
