import { Container, Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import "../pages";
import Footer from "../components/Footer"; // Importamos el Footer correctamente

const Home = () => {
  const navigate = useNavigate();

  return (
    <div className="home-container">
      <Container className="text-center content">
        <h1>Bienvenido a iDrive</h1>
        <p>Gestiona tus clases de conducción fácilmente.</p>
        <Button variant="primary" size="lg" onClick={() => navigate('/login')}>
          Iniciar Sesión
        </Button>
      </Container>

      <Footer /> {/* Agregamos el Footer correctamente */}
    </div>
  );
};

export default Home;
