import { useEffect, useState } from "react";
import { Table, Container } from "react-bootstrap";
import './VerClases.css';


export default function VerClases() {
  const [clases, setClases] = useState([]);

  useEffect(() => {
    // Simulación de clases agendadas (luego se conectará con el backend)
    setClases([
      { id: 1, fecha: "2025-04-05", hora: "10:00", tipo: "Teórica" },
      { id: 2, fecha: "2025-04-07", hora: "14:00", tipo: "Práctica" },
    ]);
  }, []);

  return (
    <Container className="mt-5">
      <h2>Clases Agendadas</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Fecha</th>
            <th>Hora</th>
            <th>Tipo</th>
          </tr>
        </thead>
        <tbody>
          {clases.map((clase) => (
            <tr key={clase.id}>
              <td>{clase.id}</td>
              <td>{clase.fecha}</td>
              <td>{clase.hora}</td>
              <td>{clase.tipo}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
}
