import { Link } from "react-router-dom";

const Sidebar = () => {
  return (
    <ul className="navbar-nav bg-gradient-primary sidebar sidebar-dark accordion" id="accordionSidebar">
      {/* Logo y nombre de iDrive */}
      <Link className="sidebar-brand d-flex align-items-center justify-content-center" to="/dashboard">
        <div className="sidebar-brand-icon">
          <i className="fas fa-car"></i> {/* Ícono de carro o cualquier otro relacionado */}
        </div>
        <div className="sidebar-brand-text mx-3">iDrive</div>
      </Link>

      <hr className="sidebar-divider my-0" />

      {/* Dashboard */}
      <li className="nav-item">
        <Link className="nav-link" to="/dashboard">
          <i className="fas fa-fw fa-tachometer-alt"></i>
          <span>Dashboard</span>
        </Link>
      </li>

      <hr className="sidebar-divider" />

      {/* Agendamientos */}
      <li className="nav-item">
        <Link className="nav-link" to="/agendamientos">
          <i className="fas fa-fw fa-calendar"></i>
          <span>Agendamientos</span>
        </Link>
      </li>

      {/* Usuarios */}
      <li className="nav-item">
        <Link className="nav-link" to="/usuarios">
          <i className="fas fa-fw fa-users"></i>
          <span>Usuarios</span>
        </Link>
      </li>

      {/* Clases */}
      <li className="nav-item">
        <Link className="nav-link" to="/clases">
          <i className="fas fa-fw fa-car"></i>
          <span>Clases</span>
        </Link>
      </li>

      <hr className="sidebar-divider d-none d-md-block" />

      {/* Botón de colapsar sidebar */}
      <div className="text-center d-none d-md-inline">
        <button className="rounded-circle border-0" id="sidebarToggle"></button>
      </div>
    </ul>
  );
};

export default Sidebar;
