import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import "@fortawesome/fontawesome-free/css/all.min.css";
import "bootstrap/dist/css/bootstrap.min.css";
import "../public/js/sb-admin-2.min.js";
import Navigation from "./components/Navbar";
import Sidebar from "./components/Sidebar";
import Home from "./pages/Home";
import Dashboard from "./pages/Dashboard";
import Login from "./pages/Login";
import AgendarClase from "./pages/AgendarClase";
import VerClases from "./pages/VerClases";

function App() {
  return (
    <Router>
      <Routes>
        {/* Rutas fuera del dashboard (sin sidebar) */}
        <Route path="/" element={<><Navigation /><Home /></>} />
        <Route path="/login.js" element={<><Navigation /><Login /></>} />

        {/* Rutas dentro del dashboard (con sidebar) */}
        <Route
          path="/dashboard"
          element={
            <div id="wrapper">
              <Sidebar />
              <div id="content-wrapper" className="d-flex flex-column">
                <div id="content">
                  <Navigation />
                  <Dashboard />
                </div>
              </div>
            </div>
          }
        />
        <Route
          path="/agendar"
          element={
            <div id="wrapper">
              <Sidebar />
              <div id="content-wrapper" className="d-flex flex-column">
                <div id="content">
                  <Navigation />
                  <AgendarClase />
                </div>
              </div>
            </div>
          }
        />
        <Route
          path="/clases"
          element={
            <div id="wrapper">
              <Sidebar />
              <div id="content-wrapper" className="d-flex flex-column">
                <div id="content">
                  <Navigation />
                  <VerClases />
                </div>
              </div>
            </div>
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
