import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [usuarios, setUsuarios] = useState([]);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Verificar si el usuario está autenticado
    if (!localStorage.getItem("auth")) {
      navigate("/login"); // Redirigir al Login si no está autenticado
      return;
    }

    // Obtener lista de usuarios
    fetch("http://localhost:8000/Usuarios")
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Error ${response.status}: No se pudo obtener usuarios`);
        }
        return response.json();
      })
      .then((data) => setUsuarios(data))
      .catch((error) => {
        console.error("Error en la solicitud:", error);
        setError(error.message);
      });
  }, [navigate]);

  // Función para cerrar sesión
  const handleLogout = () => {
    localStorage.removeItem("auth"); // Eliminar sesión
    navigate("/"); // Redirigir a la página de inicio
  };

  return (
    <div>
      <h1>Lista de Usuarios</h1>
      
      <button onClick={handleLogout} style={{ marginBottom: "20px", backgroundColor: "red", color: "white", padding: "10px", border: "none", cursor: "pointer" }}>
        Cerrar Sesión
      </button>

      {error ? (
        <p style={{ color: "red" }}>{error}</p>
      ) : (
        <ul>
          {usuarios.length > 0 ? (
            usuarios.map((usuario) => (
              <li key={usuario.id}>{usuario.nombre} - {usuario.email}</li>
            ))
          ) : (
            <p>Cargando usuarios...</p>
          )}
        </ul>
      )}
    </div>
  );
};

export default Dashboard;
