import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import "./Agendamientos.css";
import Sidebar from "../components/Sidebar";

const Agendamientos = () => {
  const [agendamientos, setAgendamientos] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8000/Agendamientos/")
      .then((response) => response.json())
      .then((data) => {
        setAgendamientos(data);
      })
      .catch((error) => console.error("Error fetching agendamientos:", error));
  }, []);

  const eventos = agendamientos.map((agendamiento) => ({
    title: `${agendamiento.nombre_clase} (${agendamiento.estudiante})`,
    date: agendamiento.fecha_reserva,
  }));

  return (
    <div className="wrapper">
      <Sidebar />
      <div className="content p-4">
        <h2 className="mb-4">Calendario de Agendamientos</h2>

        <div className="d-flex justify-content-end mb-3">
          <button className="btn btn-primary me-2" onClick={() => console.log("Agregar")}>
            <i className="fas fa-plus"></i> Agendar Clase
          </button>
          <button className="btn btn-warning me-2" onClick={() => console.log("Modificar")}>
            <i className="fas fa-edit"></i> Modificar Agendamiento
          </button>
          <button className="btn btn-danger" onClick={() => console.log("Eliminar")}>
            <i className="fas fa-trash"></i> Cancelar Agendamiento
          </button>
        </div>

        <FullCalendar
          plugins={[dayGridPlugin]}
          initialView="dayGridMonth"
          events={eventos}
          locale="es"
          height="auto"
        />
      </div>
    </div>
  );
};

export default Agendamientos;
